package week16;

public class integer {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1234;
		
		System.out.println(i+"을 2진수로 표현 : "+Integer.toBinaryString(i));
		System.out.println(i+"을 8진수로 표현 : "+Integer.toOctalString(i));
		System.out.println(i+"을 16진수로 표현 : "+Integer.toHexString(i));
		

	}

}
